import React from 'react'

export default function ToDoItem(props) {

    console.log("todo = ", props.todo.todo_title)
    console.log("onClickFunction = ", props.onClickFunction)
    return (

        <div>

            <div class="notification is-primary">
                {/* <div class="is-size-5	">{props.todo.todo_title}</div> */}
                <button class="delete" onClick={() => props.onClickFunction(props.todo.todo_id)}></button>
                {props.todo.todo_data}
                <p class="level-item"> {props.todo.created_at}</p>
                <button class="button" onClick={() => props.handleModalDisplay(props.todo.todo_id)}>Edit</button>
            </div>

        </div>


    )
}
