import React, { Component } from 'react';
import './index.scss';
// import Loader from 'react-loader-spinner';
import APIURLS from '../../api-urls/to-do-api';

export class LoginPage extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isauth: false,
			chart: null,
			userName: null,
			isError:false,
			errorMsg:''
		};

		this.handleKeyDown = this.handleKeyDown.bind(this);
	}
	componentDidMount() {
		localStorage.clear();
	}

	componentDidUpdate(prevProps, prevState) {
		if (this.props.Token !== null && this.props.Token !== '' && prevProps.Token !== this.props.Token) {
			this.props.history.push('/Dashboard/People');
		}
	}

	grantAccess = () => {
		fetch(APIURLS.V1.validateUser, {
			method: 'POST',
			mode: 'cors',
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify({
				data: {
					username: this.props.UserName,
					password: this.props.passWord
				}
			})
		})
			.then((response) => response.json())
			.then((json) => {
				this.setState({
					isauth: json['data']['isauth']
				});
			});
	};

	handleKeyDown(e) {
		if (e.key === 'Enter') {
			var userNameNode = document.getElementById('username').value;
			var userPermNode = document.getElementById('password').value;

			if (
				userNameNode !== null &&
				userPermNode !== null &&
				userNameNode.trim() !== '' &&
				userPermNode.trim() !== ''
			) {
				var input = document.getElementById('myInput');

				input.addEventListener('keyup', function(event) {
					if (event.keyCode === 13) {
						event.preventDefault();
						document.getElementById('myBtn').click();
					}
				});

				fetch(APIURLS.V1.validateUser, {
					method: 'POST',
					mode: 'cors',
					headers: {
						'Content-Type': 'application/json'
					},
					body: JSON.stringify({
						data: {
							username: userNameNode,
							password: userPermNode
						}
					})
				})
					.then((response) => response.json())
					.then((json) => {
						this.setState({
							isauth: json['data']['isauth']
						});

						if (json['data']['token']) {
							localStorage.setItem('Nikhil', json['data']['token']);
						} else {
							this.setState({
								isError:true,
								errorMsg:"Please enter valid credentials"
							})
						}

						this.props.getUserDetails(
							json['data']['username'],
							json['data']['password'],
							this.state.isauth,
							json['data']['token']
						);
					});
			}
		}
	}

	getUserDetailsData = () => {
        console.log("-->")	
		var userNameNode = document.getElementById('username').value;
		var userPermNode = document.getElementById('password').value;

		if (
			userNameNode !== null &&
			userPermNode !== null &&
			userNameNode.trim() !== '' &&
			userPermNode.trim() !== ''
		) {
			var input = document.getElementById('myInput');

			input.addEventListener('keyup', function(event) {
				if (event.keyCode === 13) {
					event.preventDefault();
					document.getElementById('myBtn').click();
				}
			});

			fetch(APIURLS.V1.validateUser, {
				method: 'POST',
				mode: 'cors',
				headers: {
                    'Content-Type': 'application/json',
                    'Authorization':'Token d3d2698139ce5e4fdfb925d903e26d223d4a3920'
				},
				body: JSON.stringify({

						username: userNameNode,
						password: userPermNode

				})
			})
				.then((response) => response.json())
				.then((json) => {	
                    console.log("-->",json)				
					this.setState({
						isauth: json['data']['isauth']
					});

					if (json['data']['token']) {
						localStorage.setItem('Nikhil', json['data']['token']);
						this.props.getUserDetails(json['data']['username'], json['data']['password'], this.state.isauth);
						this.props.history.push('/Dashboard/People');
					} 
					else 
					{
						this.setState({
							isError:true,
							errorMsg:"Please enter valid credentials"
						})
					}


				});
		}
	};

	render() {
		const LoginBoxContent = () => {
			return (
				<div>
					<div className="sufficiency-of-input ">
						<p className="heading-font">People-centric Hub for Colgate North America</p>
					</div>
					<div>
						{' '}
						<div className="card-box-margin">
							<div>
								<form action="" className="box bg-color-box">
									<div className="field">
										<label for="" className="label">
											UserName
										</label>
										<div className="control has-icons-left bg-color-box">
											<input
												id="username"
												type="email"
												placeholder="e.g. username@fractal.ai"
												className="input"
											/>
											<span className="icon is-small is-left">
												<i className="fa fa-envelope" />
											</span>
										</div>
									</div>
									<div className="field">
										<label for="" className="label">
											Password
										</label>
										<div className="control has-icons-left bg-color-box">
											<input
												id="password"
												type="password"
												placeholder="*******"
												className="input"
												required
												onKeyDown={this.handleKeyDown}
											/>
											<span className="icon is-small is-left">
												<i className="fa fa-lock" />
											</span>
										</div>
									</div>
									<div className="field">
										<a
											href
											id="myInput"
											className="button is-info"
											onClick={() => this.getUserDetailsData()}
										>
											Login
										</a>										
									</div>
									{this.state.isError ? <div className="error-msg">{this.state.errorMsg}</div> : null}
								</form>
							</div>
						</div>
					</div>
					<div />
				</div>
			);
		};
		if (this.state.isauth === false) {
			return (
				<div className="container-fluid home">
					<nav className="navbar navbar-align nav-home" role="navigation" aria-label="main navigation">
						<div className="navbar-brand" />
					</nav>
					<LoginBoxContent showDetails={this.state.showModal} closeModal={this.toggleAnalyzerModal} />
				</div>
			);
		}
		else
		{		
        return "test"	
		// return<Loader
		// 		className="loader-display"
		// 		type="Oval"
		// 		color="#001e59"
		// 		height={100}
		// 		width={100}				
		// 		visible={true}
		// 	/>
		}
	}
}

