import React, { Component } from 'react';
import './index.scss';

export class ToDo extends Component {
	state = {
		question: '',
		answer: '',
		responseList: []
	};


	clearAll = () => {

		localStorage.clear();
	};

	handleChange = (event) => {
		this.setState({
			[event.target.name]: event.target.value,
			question: event.target.value
		});
	};

	clearData = () => {
		this.setState({
			question: ' ',
			answer: '',
			responseList: []

		});
	};

	onEnterClickAction() {
		fetch('http://localhost:8000/api/v1/bot', {
			method: 'POST',
			mode: 'cors',
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify({
				data: this.state.question
			})
		})
			.then((response) => response.json())
			.then((json) => {
				if (json['res'] !== undefined) {
					var responseListData = this.state.responseList;
					this.setState({
						answer: json['res']
					});

					responseListData.push({
						question: this.state.question,
						answer: this.state.answer
					});

					this.setState({
						answer: '',
						responseList: responseListData
					});
				}
			});
	}

	render() {
		return (
			<div>
				<header>
					<nav class="navbar" role="navigation" aria-label="main navigation">
						<div class="navbar-brand">
							<a class="navbar-item" href="/ToDo">
								<img alt ="" src="https://img.icons8.com/fluent/48/000000/todo-list.png" />
							</a>
							<text class="navbar-item">
								To-Do
      </text>


						</div>

						<div id="navbarBasicExample" class="navbar-menu">
							<div class="navbar-start">


							</div>
						</div>

						<div class="navbar-end">
							<div class="navbar-item">
								<div class="buttons">

									<a href="/" class="button is-light">
										Log Out
          </a>
								</div>
							</div>
						</div>
					</nav>
				</header>

				<article class="box">
					{this.state.responseList.map((elementData) => {
						return (
							<article class="box">
								<nav class="level is-mobile">
									<div class="level-left">Questions :-  {elementData['question']}</div>
								</nav>
								<div>Chatbot: {elementData['answer']}</div>
							</article>
						);
					})}
					<nav class="level is-mobile">
						<div class="level-left">
							Questions:-{' '}
							<input
								class="input search-bar"
								type="text"
								// placeholder={this.props.KeyWord}
								onChange={this.handleChange}
							/>
						</div>
						<div class="level-right">
							<span><button className="button info" onClick={() => this.onEnterClickAction()}>
								SEND
							</button>
							</span>
							<span><button className="button info" onClick={() => this.clearData()}>
								Clear
							</button>
							</span>
						</div>
					</nav>
					{/* <div>
						Chatbot:
						{this.state.answer !== '' ? this.state.answer : null}
					</div> */}
				</article>
			</div>
		);
	}
}

export default ToDo;
