import React, { Component } from 'react';
import './index.scss';
import ToDoItem from './to-do-item';
import APIURLS from '../../api-urls/to-do-api';

export class ToDo extends Component {

	constructor(props){

		super(props);
		this.state = {
			toggleModal: false,
			text_data: '',
			answer: '',
			responseList: []
		};
		this.handleModalDisplay = this.handleModalDisplay.bind(this);
		this.onClickFunction = this.onClickFunction.bind(this);	
	}

	handleModalDisplay(value) {		
			this.setState((prev) => {
				return { toggleModal: !prev.toggleModal};
			});		
	}


	componentDidMount() {
		if (localStorage.getItem("key") === null) {
			this.props.history.push('/');
			localStorage.clear();
		}
		else {

			fetch(APIURLS.V1.gettodos, {
				method: 'GET',
				mode: 'cors',
				headers: {
					'Content-Type': 'application/json',
					'Authorization': 'Token ' + localStorage.getItem("key")

				},

			})
				.then((response) => response.json())
				.then((json) => {
					console.log("-->", json)
					this.setState({
						key: json['key']
					});
					console.log("Aya 1")
					if (json['response'] !== undefined) {

						this.setState({
							responseList: json['response']
						});
					}
					else {
						this.setState({
							isError: true,
							errorMsg: "Please enter valid credentials"
						})
					}


				});


		}
	}



	clearAll = () => {

		localStorage.clear();
		this.props.history.push('/');
	};

	handleChange = (event) => {
		this.setState({
			[event.target.name]: event.target.value,
			text_data: event.target.value
		});
	};

	clearData = () => {
		this.setState({
			text_data: ' ',
			answer: '',
			responseList: []

		});
	};

	onClearClickAction() {	

			this.setState({
				text_data:' '
					});
	}

	onEnterClickAction() {													
		fetch(APIURLS.V1.savetodos, {
			method: 'POST',
			mode: 'cors',
			headers: {
				'Content-Type': 'application/json',
				'Authorization': 'Token ' + localStorage.getItem("key")
			},
			// body:formData
			body: JSON.stringify({
				todo_title : "Test To Do",
				todo_data : this.state.text_data
			})
		})
			.then((response) => response.json())
			.then((json) => {

				if(json['response'] !== undefined)
				{
					console.log("Mandge",json)
					fetch(APIURLS.V1.gettodos, {
						method: 'GET',
						mode: 'cors',
						headers: {
							'Content-Type': 'application/json',
							'Authorization': 'Token ' + localStorage.getItem("key")

						},

					})
						.then((response) => response.json())
						.then((json) => {
							console.log("-->", json)
							this.setState({
								key: json['key']
							});
							console.log("Aya 1")
							if (json['response'] !== undefined) {

								this.setState({
									responseList: json['response']
								});
							}
							else {
								this.setState({
									isError: true,
									errorMsg: "Please enter valid credentials"
								})
							}


						});
				}


				// if (json['res'] !== undefined) {
				// 	var responseListData = this.state.responseList;
				// 	this.setState({
				// 		answer: json['res']
				// 	});

				// 	responseListData.push({
				// 		text_data: this.state.text_data,
				// 		answer: this.state.answer
				// 	});

				// 	this.setState({
				// 		answer: '',
				// 		responseList: responseListData
				// 	});
				// }
			});
	}
	onClickFunction(todo_id) {		
		if (todo_id !== undefined) {

		}
	}


	render() {

		const ModalDescription = (props) => {
			if (props.toggleModal === true) {
				return (
					<div class="modal">
  <div class="modal-background"></div>
  <div class="modal-card">
    <header class="modal-card-head">
      <p class="modal-card-title">Do You Wanted To Delete This ToDo?</p>
      <button class="delete" aria-label="close"></button>
    </header>
    <section class="modal-card-body">
	<button class="button is-success">Yes</button>
      <button class="button">Cancel</button>

    </section>
    {/* <footer class="modal-card-foot">
      <button class="button is-success">Save changes</button>
      <button class="button">Cancel</button>
    </footer> */}
  </div>
</div>
				)
			}
		}
		return (
			<div>
				<header>
					<nav class="navbar" role="navigation" aria-label="main navigation">
						<div class="navbar-brand">
							<a class="navbar-item" href="/ToDo">
								<img alt="" src="https://img.icons8.com/fluent/48/000000/todo-list.png" />
							</a>
							<text class="navbar-item">
								To-Do
      </text>


						</div>

						<div id="navbarBasicExample" class="navbar-menu">
							<div class="navbar-start">


							</div>
						</div>

						<div class="navbar-end">
							<div class="navbar-item ">
								<a href >Hi {localStorage.getItem("user_name") == null ? "User" : localStorage.getItem("user_name").toUpperCase()}</a></div>
							<div class="navbar-item">

								<div class="buttons">

									<button onClick={this.clearAll} class="button is-light">
										Log Out
          </button>
								</div>
							</div>
						</div>
					</nav>
				</header>

				<article class="box">
					{this.state.responseList.map((elementData) => {
						return (
							<ToDoItem todo={elementData} onClickFunction={this.onClickFunction} />

						);
					})}
					<div >
						<textarea class="textarea" placeholder="e.g. Hello world"
								// placeholder={this.props.KeyWord}
								onChange={this.handleChange}></textarea>

						</div>
						<div class="level-right">
							<span><button className="button info" onClick={() => this.onEnterClickAction()}>
								Save
							</button>

							</span>
							{/* <span><button className="button info" onClick={() => this.onClearClickAction()}>
								Clear
							</button>

							</span> */}

						</div>
					{/* <nav class="level is-mobile">

					</nav> */}
					{/* <div>
						Chatbot:
						{this.state.answer !== '' ? this.state.answer : null}
					</div> */}
				</article>
			</div>
		);
	}
}

export default ToDo;
